function onMouseQutElementoMenu(e){limpiarActivos()}function onMouseOverElementoMenu(e){var t=e.target;limpiarActivos();t.parentNode.className="active";t.parentNode.style.opacity=.85}function onClickElementoMenu(e){var t=e.target;objeto_seleccionado=t.parentNode;limpiarActivos();t.parentNode.className="active"}function limpiarActivos(){for(var e=1;e<menu_elementos.length;e++){if(objeto_seleccionado==menu_elementos[e]){menu_elementos[e].className="active";menu_elementos[e].style.opacity=1}else{menu_elementos[e].className="";menu_elementos[e].style.opacity=1}}}var menu_elementos=document.getElementById("barra").getElementsByTagName("li");var objeto_seleccionado=menu_elementos[0];for(var i=1;i<menu_elementos.length;i++){menu_elementos[i].addEventListener("click",onClickElementoMenu,false);menu_elementos[i].addEventListener("mouseover",onMouseOverElementoMenu,false);menu_elementos[i].addEventListener("mouseout",onMouseQutElementoMenu,false)}